/* é¡µé¢è®¡æ—¶å™¨ start */
let now = new Date();
function createtime() {
    // å½“å‰æ—¶é—´
    now.setTime(now.getTime() + 1000);
    let start = new Date("05/14/2023 00:00:00"); // æ—…è¡Œè€…1å·å¼€å§‹è®¡ç®—çš„æ—¶é—´
    let dis = Math.trunc(23400000000 + (now - start) / 1000 * 17); // è·ç¦»=ç§’æ•°*é€Ÿåº¦ è®°ä½è½¬æ¢æ¯«ç§’
    let unit = (dis / 149600000).toFixed(6); // å¤©æ–‡å•ä½

    // ç½‘ç«™è¯ç”Ÿæ—¶é—´
    let grt = new Date("03/01/2025 00:00:00");
    let days = (now - grt) / 1e3 / 60 / 60 / 24;
    let dnum = Math.floor(days);
    let hours = (now - grt) / 1e3 / 60 / 60 - 24 * dnum;
    let hnum = Math.floor(hours);
    let minutes = (now - grt) / 1e3 / 60 - 1440 * dnum - 60 * hnum;
    let mnum = Math.floor(minutes);
    let seconds = (now - grt) / 1e3 - 86400 * dnum - 3600 * hnum - 60 * mnum;
    let snum = Math.round(seconds);

    let currentTimeHtml = "";
    currentTimeHtml = `<div style="font-size:13px;font-weight:bold">
        æœ¬ç«™å±…ç„¶è¿è¡Œäº† ${dnum} å¤© ${hnum} å°æ—¶ ${mnum} åˆ† ${snum} ç§’ 
        <i id="heartbeat" class="fas fa-heartbeat"></i><br>
        æ—…è¡Œè€… 1 å·å½“å‰è·ç¦»åœ°çƒ ${dis} åƒç±³,çº¦ä¸º ${unit} ä¸ªå¤©æ–‡å•ä½ ğŸš€</div>
        <font size=2px>å†çœ‹çœ‹é‚£ä¸ªå…‰ç‚¹,å®ƒå°±åœ¨è¿™é‡Œ,è¿™æ˜¯å®¶å›­,è¿™æ˜¯æˆ‘ä»¬â€”â€” 
        ä½ æ‰€çˆ±çš„æ¯ä¸€ä¸ªäºº,ä½ è®¤è¯†çš„æ¯ä¸€ä¸ªäºº,ä½ å¬è¯´è¿‡çš„æ¯ä¸€ä¸ªäºº<br>æ›¾ç»æœ‰è¿‡çš„æ¯ä¸€ä¸ªäºº,
        éƒ½åœ¨å®ƒä¸Šé¢åº¦è¿‡ä»–ä»¬çš„ä¸€ç”Ÿâ­</font>`;

    document.getElementById("workboard") &&
        (document.getElementById("workboard").innerHTML = currentTimeHtml);
}

// è®¾ç½®é‡å¤æ‰§è¡Œå‡½æ•°ï¼Œå‘¨æœŸ1000ms
setInterval(() => {
    createtime();
}, 1000);

if (window.localStorage.getItem("fpson") == undefined || window.localStorage.getItem("fpson") == "1") {
    var rAF = function () {
        return (
            window.requestAnimationFrame ||
            window.webkitRequestAnimationFrame ||
            function (callback) {
                window.setTimeout(callback, 1000 / 60);
            }
        );
    }();
    var frame = 0;
    var allFrameCount = 0;
    var lastTime = Date.now();
    var lastFameTime = Date.now();
    var loop = function () {
        var now = Date.now();
        var fs = (now - lastFameTime);
        var fps = Math.round(1000 / fs);

        lastFameTime = now;
        // ä¸ç½® 0ï¼Œåœ¨åŠ¨ç”»çš„å¼€å¤´åŠç»“å°¾è®°å½•æ­¤å€¼çš„å·®å€¼ç®—å‡º FPS
        allFrameCount++;
        frame++;

        if (now > 1000 + lastTime) {
            var fps = Math.round((frame * 1000) / (now - lastTime));
            if (fps <= 5) {
                var kd = `<span style="color:#bd0000">å¡æˆpptğŸ¤¢</span>`
            } else if (fps <= 15) {
                var kd = `<span style="color:red">ç”µç«çº§å¸§ç‡ğŸ˜–</span>`
            } else if (fps <= 25) {
                var kd = `<span style="color:orange">æœ‰ç‚¹éš¾å—ğŸ˜¨</span>`
            } else if (fps < 35) {
                var kd = `<span style="color:#9338e6">ä¸å¤ªæµç•…ğŸ™„</span>`
            } else if (fps <= 45) {
                var kd = `<span style="color:#08b7e4">è¿˜ä¸é”™å“¦ğŸ˜</span>`
            } else {
                var kd = `<span style="color:#39c5bb">ååˆ†æµç•…ğŸ¤£</span>`
            }
            document.getElementById("fps").innerHTML = `FPS:${fps} ${kd}`;
            frame = 0;
            lastTime = now;
        };

        rAF(loop);
    }

    loop();
} else {
    document.getElementById("fps").style = "display:none!important"
}

function setMask() {
    //è®¾ç½®é®ç½©
    if (document.getElementsByClassName("rmMask")[0] != undefined)
        return document.getElementsByClassName("rmMask")[0];
    mask = document.createElement('div');
    mask.className = "rmMask";
    mask.style.width = window.innerWidth + 'px';
    mask.style.height = window.innerHeight + 'px';
    mask.style.background = '#fff';
    mask.style.opacity = '.0';
    mask.style.position = 'fixed';
    mask.style.top = '0';
    mask.style.left = '0';
    mask.style.zIndex = 998;
    document.body.appendChild(mask);
    document.getElementById("rightMenu").style.zIndex = 19198;
    return mask;
}

function insertAtCursor(myField, myValue) {

    //IE æµè§ˆå™¨
    if (document.selection) {
        myField.focus();
        sel = document.selection.createRange();
        sel.text = myValue;
        sel.select();
    }

    //FireFoxã€Chromeç­‰
    else if (myField.selectionStart || myField.selectionStart == '0') {
        var startPos = myField.selectionStart;
        var endPos = myField.selectionEnd;

        // ä¿å­˜æ»šåŠ¨æ¡
        var restoreTop = myField.scrollTop;
        myField.value = myField.value.substring(0, startPos) + myValue + myField.value.substring(endPos, myField.value.length);

        if (restoreTop > 0) {
            myField.scrollTop = restoreTop;
        }

        myField.focus();
        myField.selectionStart = startPos + myValue.length;
        myField.selectionEnd = startPos + myValue.length;
    } else {
        myField.value += myValue;
        myField.focus();
    }
}

let rmf = {};
rmf.showRightMenu = function (isTrue, x = 0, y = 0) {
    let $rightMenu = $('#rightMenu');
    $rightMenu.css('top', x + 'px').css('left', y + 'px');

    if (isTrue) {
        $rightMenu.show();
    } else {
        $rightMenu.hide();
    }
}

rmf.copyWordsLink = function () {
    let url = window.location.href
    let txa = document.createElement("textarea");
    txa.value = url;
    document.body.appendChild(txa)
    txa.select();
    document.execCommand("Copy");
    document.body.removeChild(txa);
}
rmf.switchReadMode = function () {
    const $body = document.body
    $body.classList.add('read-mode')
    const newEle = document.createElement('button')
    newEle.type = 'button'
    newEle.className = 'fas fa-sign-out-alt exit-readmode'
    $body.appendChild(newEle)

    function clickFn() {
        $body.classList.remove('read-mode')
        newEle.remove()
        newEle.removeEventListener('click', clickFn)
    }

    newEle.addEventListener('click', clickFn)
}

//å¤åˆ¶é€‰ä¸­æ–‡å­—
rmf.copySelect = function () {
    document.execCommand('Copy', false, null);
}

