    data: {
        key: 'B7FBZ-X67CZ-WWXXC-7TJ4D-KVDBS-HJB2Y', // 你的腾讯地图API Key
        output: 'jsonp',
    },
    dataType: 'jsonp',
    success: function (res) {
        ipLoacation = res;
        showWelcome(); // 请求成功后调用显示欢迎信息的函数
    },
    error: function (err) {
        console.log('IP定位请求失败:', err);
    }
