                            posdesc = "那拉提草原风吹，伊宁熏马肠果子沟花海。";
                            break;
                        case "塔城地区":
                            posdesc = "额敏河水清如镜，塔城馕坑烤肉沙湾辣子鸡。";
                            break;
                        case "阿勒泰地区":
                            posdesc = "喀纳斯湖水怪传，福海烤鱼布尔津奶酪香。";
                            break;
                        case "石河子市":
                            posdesc = "军垦绿洲棉花白，石河子桃甜烤羊腿飘香。";
                            break;
                        default:
                            posdesc = "驼铃古道丝绸路，胡马犹闻唐汉风。";
                            break;
                    }
                    break;
                case "台湾省":
                    switch (ipLoacation.result.ad_info.city) {
                        case "台北市":
                            posdesc = "101大楼刺夜空，士林夜市珍珠奶茶香。";
                            break;
                        case "新北市":
                            posdesc = "九份老街灯笼明，淡水渔港卤肉饭扑鼻。";
                            break;
                        case "桃园市":
                            posdesc = "大溪豆干香满街，石门水库鱼汤鲜又甜。";
                            break;
                        case "台中市":
                            posdesc = "逢甲夜市热闹非凡，太阳饼酥台中米糕香。";
                            break;
                        case "台南市":
                            posdesc = "安平古堡诉往事，担仔面香赤崁楼夜色。";
                            break;
                        case "高雄市":
                            posdesc = "爱河夜景波光闪，旗津海鲜六合夜市香。";
                            break;
                        default:
                            posdesc = "我在这头，大陆在那头。";
                            break;
                    }
                    break;
                case "香港特别行政区":
                    posdesc = "维港夜景璀璨如星，烧鹅酥脆港式奶茶香。";
                    break;
                case "澳门特别行政区":
                    posdesc = "大三巴牌坊诉沧桑，葡式蛋挞赌城灯火明。";
                    break;
                default:
                    posdesc = "带我去你的城市逛逛吧！";
                    break;
            }
            break;
        default:
            posdesc = "带我去你的国家逛逛吧。";
            break;
    }

    // 根据本地时间切换欢迎语
    let timeChange;
    let date = new Date();
    if (date.getHours() >= 5 && date.getHours() < 11) timeChange = "<span>上午好</span>，一日之计在于晨！";
    else if (date.getHours() >= 11 && date.getHours() < 13) timeChange = "<span>中午好</span>，该摸鱼吃午饭了。";
    else if (date.getHours() >= 13 && date.getHours() < 15) timeChange = "<span>下午好</span>，懒懒地睡个午觉吧！";
    else if (date.getHours() >= 15 && date.getHours() < 16) timeChange = "<span>三点几啦</span>，一起饮茶呀！";
    else if (date.getHours() >= 16 && date.getHours() < 19) timeChange = "<span>夕阳无限好！</span>";
    else if (date.getHours() >= 19 && date.getHours() < 24) timeChange = "<span>晚上好</span>，夜生活嗨起来！";
    else timeChange = "夜深了，早点休息，少熬夜。";

    // 将欢迎信息写入页面
    try {
        document.getElementById("welcome-info").innerHTML =
            `<b><center>🎉 欢迎信息 🎉</center>  来自 <span style="color:#97e6f2">${pos}</span> 的小伙伴，${timeChange}您现在距离俺约 <span style="color:#97e6f2">${dist}</span> 公里，当前的IP地址为： <span style="color:#97e6f2">${ip}</span>， ${posdesc}</b>`;
    } catch (err) {
        console.log("无法获取#welcome-info元素:", err);
    }
}

// 页面加载时执行
window.onload = showWelcome;

// 如果使用Pjax，监听Pjax完成事件
document.addEventListener('pjax:complete', showWelcome);
